<style>
.loader-container{
    display: flex;
    flex-direction: row;
    height: 100%;
    width: 100%;
}
.infinity{
    height: 100%;
    stroke-dasharray: 1450;
    animation: infinity-spinner 3s linear infinite
}

@keyframes infinity-spinner{
    to{
        stroke-dashoffset: 20000;
    }
}
.default-loader{
  border: 2px solid #dbdbdb; /* Light grey */
  border-top: 2px solid; /* Blue */
  width: 100%;
  height: 100%;
  border-radius: 50%;
  animation: default-spin 1s linear infinite;
}

@keyframes default-spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}
.bubbles-loader{
  display: flex;
  justify-content: space-between;
  flex-direction: row;
  width: 100%;
  height: 100%;

}
.bubbles-loader div{
  width: 25%;
  height: 25%;
  border-radius: 50%;
  opacity: 0.5;
  transition: 1s ease;
}
.bubbles-loader div:nth-child(1){
  animation: bubbles-expand 0.5s ease infinite;
}
.bubbles-loader div:nth-child(2){
  animation: bubbles-expand-2 0.5s ease infinite;
}
.bubbles-loader div:nth-child(3){
  animation: bubbles-expand 0.5s ease infinite;
}

@keyframes bubbles-expand {
  0% { transform: scale(1);
  opacity: 0.5; }
  50% {  transform: scale(1.5);
  opacity: 1; }
  100% { transform: scale(1);
  opacity: 0.5;}
}
@keyframes bubbles-expand-2 {
  0% { transform: scale(1.5);
  opacity: 0.5; }
  50% {  transform: scale(1);
  opacity: 1; }
  100% { transform: scale(1.5);
  opacity: 0.5;}
}
.ball-loader-container{
  display: flex;
  justify-content: center;
}
.ball-loader{
  height: 100%;
  width: 100%;
  overflow: visible;
}
.ball-loader circle{
  animation: ball-bounce 1s cubic-bezier(0.96, -0.77, 0.8, 0.34) infinite;
}
@keyframes ball-bounce{
  0%{
    transform: translate(0, -50vh);
  }
  50%{
    transform: scale3d(1.2,0.9, 1);

  }
  100%{
    transform: translate(0, -50vh);
  }
}
.bubble-spin{
  width: 100%;
  height: 100%;
  overflow: visible;
}
.bubble-spin g path{
  transform-origin: center;
  animation: circle-bubble-loader 0.8s linear infinite;
}
.bubble-spin g path:nth-child(1){
  animation-delay: 0s;
}
.bubble-spin g path:nth-child(2){
  animation-delay: -0.1s;
}
.bubble-spin g path:nth-child(3){
  animation-delay: -0.2s;
}
.bubble-spin g path:nth-child(4){
  animation-delay: -0.3s;
}
.bubble-spin g path:nth-child(5){
  animation-delay: -0.4s;
}
.bubble-spin g path:nth-child(6){
  animation-delay: -0.5s;
}
.bubble-spin g path:nth-child(7){
  animation-delay: -0.6s;
}
.bubble-spin g path:nth-child(8){
  animation-delay: -0.7s;
}
@keyframes circle-bubble-loader{
  0%, 20%, 80%, 100%{
    opacity: 0.5;
    transform: scale(1);
  }
  50%{
    opacity: 1;
    transform: scale(1.2);
  }

}
.loading-text{
  width: 100%;
  height: 100%;
}
.loading-text g path{
  animation: text-bounce 3s linear infinite;
}
.loading-text #letters path:nth-child(7){
  animation-delay: 0s;
}
.loading-text #letters path:nth-child(6){
  animation-delay: -0.1s;
}
.loading-text #letters path:nth-child(5){
  animation-delay: -0.2s;
}
.loading-text #letters path:nth-child(4){
  animation-delay: -0.3s;
}
.loading-text #letters path:nth-child(3){
  animation-delay: -0.4s;
}
.loading-text #letters path:nth-child(2){
  animation-delay: -0.5s;
}
.loading-text #letters path:nth-child(1){
  animation-delay: -0.6s;
}
.loading-text #dots path:nth-child(3){
  animation-delay: 0.3s;
}
.loading-text #dots path:nth-child(2){
  animation-delay: 0.2s;
}
.loading-text #dots path:nth-child(1){
  animation-delay: 0.1s;
}

@keyframes text-bounce{
  0%, 20%, 100%{
    transform:  translateY(0);
  }
  10%{
    transform:  translateY(-30%);
  }
}
.rain-drop{
  width: 100%;
  height: 100%;
  display: inline-block;
  position: relative;
  top: 0;
}
.rain-drop div{
  position: absolute;
  height: 100%;
  width: 100%;
  border-radius: 50%;
  border: 4px solid;
  background-color: transparent;
  animation: raindrop-droptop 1s linear infinite;
}
.rain-drop div:nth-child(1){
  animation-delay: 0s;
}

.rain-drop div:nth-child(2){
  animation-delay: -0.5s;
}
@keyframes raindrop-droptop {
  0%{
    opacity: 0.5;
    transform: scale(0);
  }
  100%{
    opacity: 0;
    transform: scale(1);
  }
}
.gears-loader{
  height: 100%;
  width: 100%;
}
.gears-loader #gear1 path, .gears-loader #gear2 path{
  animation: cogwheels-turning 1.5s linear infinite;
  transform-box: fill-box;
  transform-origin: center;
  transform-origin: 50% 50%;
}
.gears-loader #gear2 path{
  animation: cogwheels-turning-reverse 1.5s linear infinite;
}
@keyframes cogwheels-turning{
  0%{
    transform: rotate(0deg)
  }
  100%{
    transform: rotate(360deg)
  }
}
@keyframes cogwheels-turning-reverse{
  0%{
    transform: rotate(0deg)
  }
  100%{
    transform: rotate(-360deg)
  }
}

</style>
